import styles from './Element.module.css';
import React, { forwardRef, useEffect, useState } from 'react';
import { useXarrow } from 'react-xarrows';

const Element = forwardRef(
  (
    {
      content,
      isDouble = false,
      isDetailed = false,
      style = {},
      className = {},
      delayed = false,
      interval = 3000,
    },
    ref
  ) => {
    const [showContent, setShowContent] = useState(false);

    useEffect(() => {
      if (delayed) {
        const timeoutId = setTimeout(() => {
          setShowContent(true);
        }, interval);

        return () => clearTimeout(timeoutId);
      } else {
        setShowContent(true);
      }
    }, [delayed]);

    if (isDetailed) {
      return (
        <div
          className={`${className} ${styles.container} ${styles.detailed} ${
            isDouble ? styles.double : ''
          }`}
          ref={ref}
          onClick={useXarrow()}
        >
          <div>
            {showContent && <div className={styles.content}>{content}</div>}
            {!showContent && <div className={styles.content}>{''}</div>}
          </div>
          <div style={{ display: isDouble ? 'block' : 'none' }}>
            <div className={`${styles.pointer}`}>
              <p>*ant</p>
            </div>
          </div>
          <div>
            <div className={`${styles.pointer}`}>
              <p>*prox</p>
            </div>
          </div>
        </div>
      );
    } else {
      return (
        <div
          style={style}
          className={`${className} ${styles.container}`}
          ref={ref}
          onClick={useXarrow()}
        >
          {showContent && <div className={styles.content}>{content}</div>}
          {!showContent && <div className={styles.content}>{''}</div>}
        </div>
      );
    }
  }
);

export default Element;
