import React from 'react';
import styles from './CodePanel.module.css';

function CodeLine(props) {
  const { text, lineNumber, highlighted } = props;

  return (
    <div className={highlighted ? styles.CodeLineHighlighted : styles.CodeLine}>
      <span className={styles.lineNumber}>{lineNumber}</span>
      <span className={styles.codeText}>{text}</span>
    </div>
  );
}

function CodePanel(props) {
  const { code, highlightedLine } = props;

  const lines = code.split('\n').map((line, i) => {
    return <CodeLine key={i} text={line} lineNumber={i + 1} highlighted={highlightedLine === i + 1} />;
  });

  return (
    <div className={styles.CodePanel}>
      <pre>{lines}</pre>
    </div>
  );
}

export default CodePanel;
