import React, { useRef, useState } from "react";

const CodeWithGaps = ({ code, gaps }) => {
  const [draggedGap, setDraggedGap] = useState("");

  const handleDragStart = (event, gap) => {
    event.dataTransfer.setData("text/plain", gap);
    setDraggedGap(gap);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleDrop = (event, gapIndex) => {
    event.preventDefault();
    const gapElement = gapRefs.current[gapIndex];
    //gapElement.focus();
    gapElement.setRangeText(draggedGap);
    gapElement.dispatchEvent(new Event("input", { bubbles: true }));
    setDraggedGap("");
  };

  const gapRefs = useRef([]);

  const replaceStrings = (code, gaps) => {
    let result = code;

    gaps.forEach((gap, index) => {
      const regex = new RegExp(gap, "g");
      const gapPlaceholder = `{{${index}}}`;
      result = result.replace(regex, gapPlaceholder);
    });

    return result;
  };

  const codeWithGaps = replaceStrings(code, gaps);

  return (
    <div style={{ display: "flex" }}>
      <pre>
        <code>
          {/* ...existing code... */}
          {codeWithGaps.split("{{").map((line, index) => {
            if (line.includes("}}")) {
              const [gapIndex, remaining] = line.split("}}");
              const gap = gaps[parseInt(gapIndex)];
              return (
                <React.Fragment key={index}>
                  <input
                    ref={(el) => (gapRefs.current[parseInt(gapIndex)] = el)}
                    type="text"
                    draggable="false"
                    readOnly
                    defaultValue=""
                    onDragOver={handleDragOver} // Add onDragOver event handler
                    onDrop={(e) => handleDrop(e, parseInt(gapIndex))} // Add onDrop event handler
                  />
                  {remaining}
                </React.Fragment>
              );
            }
            return line;
          })}
        </code>
      </pre>
      <div style={{ marginLeft: "auto", padding: "10px" }}>
        {gaps.map((gap, index) => (
          <div
            key={index}
            draggable="true"
            onDragStart={(e) => handleDragStart(e, gap)}
            onDragOver={handleDragOver}
            onDrop={(e) => handleDrop(e, index)}
            style={{
              border: "1px solid #000",
              borderRadius: "5px",
              padding: "5px",
              marginBottom: "5px",
              cursor: "move"
            }}
          >
            {gap}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CodeWithGaps;
