import styles from "./Element.module.css";
import React, { forwardRef, useEffect, useState } from "react";
import { useXarrow } from "react-xarrows";

const Element = forwardRef(
  (
    {
      content,
      start = false,
      end = false,
      move = false,
      isDouble = false,
      style = {},
      className = {},
      delayed = false,
      interval = 3000
    },
    ref
  ) => {
    const [showContent, setShowContent] = useState(false);
    const [arrowCreated, setArrowCreated] = useState(false);

    useEffect(() => {
      if (delayed) {
        const timeoutId = setTimeout(() => {
          setShowContent(true);
        }, interval);

        return () => clearTimeout(timeoutId);
      } else {
        setShowContent(true);
      }
    }, [delayed]);

    useEffect(() => {
      if (showContent && !arrowCreated) {
        createArrow();
        setArrowCreated(true);
      }
    }, [showContent, arrowCreated]);

    const createArrow = () => {
      const arrowProps = {
        start: ref.current,
        end: document.getElementById("element2"),
        options: {
          // Customize arrow options here
        },
      };
      useXarrow(arrowProps);
    };

    const handleClick = () => {
      if (delayed) {
        setShowContent(false);
        const timeoutId = setTimeout(() => {
          setShowContent(true);
        }, interval);
        return () => clearTimeout(timeoutId);
      }
    };

    return (
      <div
        style={style}
        className={`${className} ${styles.container} ${start && styles.start} ${
          end && styles.end
        }`}
        ref={ref}
        onClick={handleClick}
      >
        <div
          className={`${styles.antPointer}`}
          style={{ display: isDouble ? "block" : "none" }}
        />
        {showContent && (
          <p className={style.content} style={{ transform: "scaleX(1)" }}>
            {content}
          </p>
        )}
        {!showContent && (
          <p className={style.content} style={{ transform: "scaleX(1)" }}>
            {""}
          </p>
        )}
        <div className={`${styles.proxPointer}`}></div>
      </div>
    );
  }
);

export default Element;
