              </div>
            </div>
          </div>
          {renderNewWorkFlow.elements.map((element, index) => element.render)}
          {renderNewWorkFlow.arrows.map((element, index) => element.render)}
          {isInitialized &&
            elementList.length === 0 &&
            !linkingNewElementStart &&
            !linkingNewElementEnd && (
              <Xarrow
                start={startRef}
                end={nullSymbolRef}
                color="black"
                path="grid"
                showHead={false}
                strokeWidth={6}
                startAnchor="right"
                endAnchor="top"
              />
            )}
        </Xwrapper>

        {/* {insertButtonClicked && (
          <motion.div
            animate={{ opacity: 1 }}
            initial={{ opacity: 0, x: 80 }}
            transition={{ duration: 1 }}
          >
            <Element content={value} delayed={true} />
          </motion.div>
        )} */}
      </div>
      <div
        style={{
          display: showNaturalLanguageExplanation ? 'block' : 'none',
          height: '50px',
          width: '100%',
          padding: '10px',
          boxSizing: 'border-box',
          backgroundColor: 'yellow',
        }}
      >
        <p>{NaturalLanguageExplanation}</p>
      </div>
      <div className={`${styles.controlPanel} text-dark`}>
        <div className="row h-100 align-items-center justify-content-center">
          <div className={`col-12 col-md-3`}>
            <div className="row justify-content-center align-content-center">
              <div className="col-2">
                <input
