import React, { useState, useEffect } from 'react';

function ArrowWithText({ list }) {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentIndex((currentIndex + 1) % list.length);
    }, 1000); // change this interval as needed

    return () => clearInterval(intervalId);
  }, [currentIndex, list]);

  return (
    <div>
      {list.map((item, index) => (
        <div key={index}>
          {index === currentIndex && (
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <div style={{ marginRight: 5 }}>&#8594;</div>
              <div>aux</div>
            </div>
          )}
          <div>{item}</div>
        </div>
      ))}
    </div>
  );
}

export default ArrowWithText;
