import React, { useState, useRef, useEffect, createRef } from 'react';
import styles from './ListPanel.module.css';
import Element from '../ListElement';
import { v4 as uuidv4 } from 'uuid';
import Seta from '../../Seta.svg';
import NullSymbol from '../../NullSymbol.svg';
import { ScrollView } from 'react-native';
import CodeBuilderModal from '../CodeBuilderModal/CodeBuilderModal';
import SetaVermelha from '../../SetaVermelha.svg';
import SetaAzul from '../../SetaAzul.svg';
import ControlPanel from '../ControlPanel';
import Xarrow, { Xwrapper, useXarrow } from 'react-xarrows';
import { motion, AnimatePresence } from 'framer-motion/dist/framer-motion';

const ListPanel = ({
  className = '',
  listType = 'simple',
  updateCode,
  updateLine,
}) => {
  const [elementList, setElementList] = useState([]);
  const [renderedList, setRenderedList] = useState([]);
  const [isInitialized, setIsInitialized] = useState(false);
  const [value, setValue] = useState('');
  const [index, setIndex] = useState('');
  const [element, setElement] = useState();
  const [valueToBeSearched, setValueToBeSearched] = useState('');
  const [indexToBeSearched, setIndexToBeSearched] = useState('');
  const [valueFound, setValueFound] = useState(true);
  const [animationStopped, setAnimationStopped] = useState(false);
  const [showNewElement, setShowNewElement] = useState(false);
  const [showDetailedElements, setShowDetailedElements] = useState(false);
  const [buildMode, setBuildMode] = useState(false);
  const [showBuildMode, setShowBuildMode] = useState(false);
  const [showNaturalLanguageExplanation, setShowNaturalLanguageExplanation] =
    useState(false);
  const [NaturalLanguageExplanation, setNaturalLanguageExplanation] =
    useState('');
  const [currentItemIndex, setCurrentItemIndex] = useState(-1);
  const [isSearchByIndex, setIsSearchByIndex] = useState(false);
  const [insertButtonClicked, setInsertButtonClicked] = useState(false);
  const [removeButtonClicked, setRemoveButtonClicked] = useState(false);
  const [functionType, setFunctionType] = useState('insertStart');
  const [modalPromise, setModalPromise] = useState(null);
  const referenceList = useRef([]);
  const startRef = useRef();
  const nullSymbolRef = useRef();
  const [itens, setItens] = useState([1, 2, 3, 4]);
  const [animationQueue, setAnimationQueue] = useState([
    {
      x: 50,
      y: 50,
      rotate: 50,
    },
    {
      x: -25,
      y: -25,
      rotate: -25,
    },
  ]);
  const [animationState, setAnimationState] = useState(animationQueue[0]);
  const [animationSpeed, setAnimationSpeed] = useState(1500);

  //new element
  const [newElementAnimation, setNewElementAnimation] = useState(false);
  // end new element
  //start searching element and animation
  const [isSearching, setIsSearching] = useState(false);
  const [searchingAnimation, setSearchingAnimation] = useState({});
  // end searching animation

  const containerRef = useRef(null);
  const elementRef = useRef(null);
  const updateXarrow = useXarrow();

  const [optionalRender, setOptionalRender] = useState(<></>);

  //----------------- my new states -----------------
  const [currentAuxIndex, setCurrentAuxIndex] = useState(null);
  const [currentAntIndex, setCurrentAntIndex] = useState(null);
  const [renderNewWorkFlow, setRenderNewWorkFlow] = useState({
    elements: [],
    arrows: [],
  });
  const [linkingNewElement, setLinkingNewElement] = useState(false);
  const [showNewPointer, setShowNewPointer] = useState(0);
  const [showRespPointer, setShowRespPointer] = useState(0);
  const [newElement, setNewElement] = useState(0);

  useEffect(() => {
    const container = containerRef.current;
    const element = elementRef.current;

    if (!container || !element) {
      return;
    }

    const containerWidth = container.clientWidth;
    const elementWidth = element.clientWidth;

    if (elementWidth > containerWidth) {
      const scrollAmount = elementWidth - containerWidth;
      container.scrollTo({ left: scrollAmount, behavior: 'smooth' });
    }
  }, []);

  useEffect(() => {
    // setInterval(() => {
    //   updateXarrow();
    // }, 500);
  }, []);

  const insereFunçaoSimples = {
    executionOrder: [
      {
        line: 6,
        animation: '',
        description:
          'A função "InsereInicio" recebe o ponteiro para a lista e o valor a ser inserido como parâmetros. Ela retornará a lista com o novo elemento;',
      },
      {
        line: 7,
        animation: 'createNewPointer',
        description:
          'O ponteiro(*) "novo" é declarado e chama a função "CriarElemento" passando o valor recebido como parâmetro.',
      },
      {
        line: 0,
        animation: '',
        description:
          'A função "CriarElemento" recebe um valor e retorna um ponteiro para o novo elemento criado.',
      },
      {
        line: 1,
        animation: 'createNewElement',
        description:
          'O ponteiro(*) "resp" é declarado e recebe o endereço de memória alocado para o novo elemento.',
      },
      {
        line: 2,
        animation: 'attributeValueToNewElement',
        description:
          'Através do ponteiro "resp" é atribuído ao novo elemento o valor recebido.',
      },
      {
        line: 3,
        animation: 'pointNewElementToNull',
        description:
          'Através do ponteiro "resp" é atribuído ao novo elemento o próximo elemento (que até o momento é NULL).',
      },
      {
        line: 4,
        animation: 'pointNewPointerToNewElement',
        description:
          'O ponteiro "resp" (que aponta para o novo elemento) é retornado pela função "CriarElemento".',
      },
      { line: 5, animation: '', timer: 500 },
      {
        line: 8,
        animation: 'pointNewElementToStart',
        description:
          'Agora que o ponteiro "novo" recebeu o novo elemento e como estamos inserindo no início da lista, diremos que o próximo elemento após o novo elemento vai ser o atual primeiro elemento da lista.',
      },
      {
        line: 9,
        animation: 'pointStartToNewElement',
        description:
          'Depois disso, diremos que o primeiro elemento da lista vai ser o novo elemento.',
      },
      {
        line: 10,
        animation: '',
        description:
          'Por fim, retornamos a lista já com o novo elemento inserido.',
      },
      { line: 11, animation: '', timer: 500 },
    ],

    code: [
      'struct elemento *CriaElemento(int numero){',
      '   struct elemento *resp = (struct elemento*) malloc(sizeof(struct elemento));',
      '   resp->valor = numero;',
      '   resp->prox = NULL;',
      '   return resp;',
      '}',
      'struct elemento *InsereInicio(elemento *lista, int numero){',
      '   struct elemento *novo = CriaElemento(numero);',
      '   novo->prox = lista;',
      '   lista = novo;',
      '   return lista;',
      '}',
    ],
  };

  // useEffect(() => {
  //   let intervalId;
  //   const intervalDuration = 2000;

  //   if (!valueFound && !animationStopped) {
  //     intervalId = setInterval(() => {
  //       setCurrentItemIndex((prevIndex) => {
  //         if (elementList.length > 0) {
  //           const index = prevIndex + 1;
  //           console.log('index: ' + index);
  //           if (index >= elementList.length) {
  //             console.log('O valor não foi encontrado');
  //             setCurrentItemIndex(-1);
  //             setAnimationStopped(true);
  //           }
  //           if (
  //             (isSearchByIndex && index == indexToBeSearched) ||
  //             elementList[index] == valueToBeSearched
  //           ) {
  //             if (insertButtonClicked) {
  //               setTimeout(() => {
  //                 insertElement(index, value);
  //                 setInsertButtonClicked(false);
  //               }, 1500);
  //             }

  //             if (removeButtonClicked) {
  //               setTimeout(() => {
  //                 if (isSearchByIndex) {
  //                   removeElement(index);
  //                   console.log('O elemento foi removido por indice');
  //                 } else {
  //                   removeElement(value, true);
  //                   console.log('O elemento foi removido por valor');
  //                 }
  //                 setRemoveButtonClicked(false);
  //               }, 1500);
  //             }

  //             console.log('O valor foi encontrado');
  //             setValueFound(true);

  //             setTimeout(() => {
  //               setCurrentItemIndex(-1);
  //             }, 2000);
  //           }

  //           return index;
  //         } else {
  //           if (insertButtonClicked) {
  //             setTimeout(() => {
  //               insertElement(index, value);
  //               setInsertButtonClicked(false);
  //             }, 1500);
  //           }

  //           return 0;
  //         }
  //       });
  //     }, intervalDuration);
  //   }

  //   return () => {
  //     clearInterval(intervalId);
  //     clearTimeout();
  //   };
  // }, [elementList.length, valueToBeSearched, valueFound, animationStopped]);

  const searchByValue = () => {
    setIsSearchByIndex(false);
    setValueToBeSearched(value);
    setValueFound(false);
    setCurrentItemIndex(-1);
    setAnimationStopped(false);
  };
  const searchByIndex = (indexNum) => {
    setIsSearchByIndex(true);
    indexNum ? setIndexToBeSearched(indexNum) : setIndexToBeSearched(index);
    setValueFound(false);
    setCurrentItemIndex(-1);
    setAnimationStopped(false);
  };

  const auxPointerStyle = {
    position: 'relative',
    left: '10px',
    textAlign: 'center',
  };

  const style = {
    initialPosition: (inRelationToElementRef) => {
      console.log(inRelationToElementRef);
      return {
        display: 'block',
        position: 'absolute',
        top: `${
          inRelationToElementRef.current
            ? inRelationToElementRef.current.getBoundingClientRect().top +
              inRelationToElementRef.current.getBoundingClientRect().height +
              30
            : 0
        }px`,
        left: `${
          inRelationToElementRef.current
            ? inRelationToElementRef.current.getBoundingClientRect().left
            : 0
        }px`,
      };
    },
    move: {},
  };

  const promisedTimeout = async (timer, callback) => {
    await new Promise((resolve) => {
      setTimeout(() => {
        resolve();
      }, timer);
    });
  };

  const promisedInterval = async (callback) => {
    await new Promise((resolve) => {
      setInterval(() => {
        callback(resolve);
      }, animationSpeed);
    });
  };

  const createNewPointer = () => {};

  const find = async (valueOrIndex, isValue) => {
    let found = false;
    let index = 0;
    setCurrentAuxIndex(index);
    await new Promise((resolve) => {
      const interval = setInterval(() => {
        if (currentAuxIndex == 'nullElement') {
          clearInterval(interval);
          resolve();
        }
        console.log('currentAuxIndex: ', currentAuxIndex);
        found = isValue
          ? elementList[index].value == valueOrIndex
          : index == valueOrIndex;
        if (!found) {
          index = index + 1 < elementList.length ? index + 1 : 'nullElement';
          setCurrentAuxIndex(index);
        } else {
          found = elementList[currentAuxIndex];
          clearInterval(interval);
          resolve();
        }
      }, animationSpeed);
      return found || null;
    });
  };

  const updateReferenceInElement = (index, newReference) => {
    setElementList((prevState) => {
      const newList = [...prevState];
      newList[index].pointerFromRef = newReference;
      return newList;
    });
  };

  const handleAnimation = async (animationList, value, index) => {
    const getAnimation = async (animation) => {
      updateLine(animation.line);
      setNaturalLanguageExplanation(animation.description || '');
      let hasWaited = false;
      switch (animation.animation) {
        case 'createNewPointer':
          const newPointerRef = createRef();
          const newPointer = {
            render: (
              <div
                style={{
                  display: 'flex',
                  position: 'absolute',
                  top: startRef.current.getBoundingClientRect().bottom + 100,
                  left: startRef.current.getBoundingClientRect().left,
                }}
                className={styles.listStart}
                ref={newPointerRef}
                key="newPointer-animation"
              >
                *Novo
              </div>
            ),
            ref: newPointerRef,
          };
          setRenderNewWorkFlow((prevState) => ({
            elements: [...prevState.elements, newPointer],
            arrows: [...prevState.arrows],
          }));
          break;
        case 'createNewElement':
          let newElementRef = createRef();
          let respPointerRef = createRef();
          let respPointer = {
            render: (
              <div
                style={{
                  display: 'flex',
                  position: 'absolute',
                  top: startRef.current.getBoundingClientRect().bottom + 250,
                  left: startRef.current.getBoundingClientRect().left + 200,
                }}
                className={styles.listStart}
                ref={respPointerRef}
                key="respPointer-animation"
              >
                *Resp
              </div>
            ),
            ref: respPointerRef,
          };
          let newElement = {
            render: (
              <Element
                style={{
                  position: 'absolute',
                  top: startRef.current.getBoundingClientRect().bottom + 100,
                  left: startRef.current.getBoundingClientRect().left + 200,
                }}
                isDetailed={showDetailedElements}
                content=""
                ref={newElementRef}
                key={uuidv4()}
              />
            ),
            ref: newElementRef,
          };
          let arrow1 = {
            render: (
              <Xarrow
                start={respPointer.ref}
                end={newElement.ref}
                color="red"
                path="straight"
                startAnchor="top"
                endAnchor="bottom"
                key={uuidv4()}
              />
            ),
          };
          setRenderNewWorkFlow((prevState) => ({
            elements: [...prevState.elements, respPointer, newElement],
            arrows: [...prevState.arrows, arrow1],
          }));
          break;
        case 'attributeValueToNewElement':
          setRenderNewWorkFlow((prevState) => {
            const newState = { ...prevState };
            newState.elements[2].ref = createRef();
            newState.elements[2].render = (
              <Element
                style={{
                  position: 'absolute',
                  top: startRef.current.getBoundingClientRect().bottom + 100,
                  left: startRef.current.getBoundingClientRect().left + 200,
                }}
                isDetailed={showDetailedElements}
                content={value.value}
                ref={newState.elements[2].ref}
                key={uuidv4()}
              />
            );
            newState.arrows[0].render = (
              <Xarrow
                start={newState.elements[1].ref}
                end={newState.elements[2].ref}
                color="red"
                path="straight"
                startAnchor="top"
                endAnchor="bottom"
                key={uuidv4()}
              />
            );
            return newState;
          });
          break;
        case 'pointNewElementToNull':
          setRenderNewWorkFlow((prevState) => {
            const newState = { ...prevState };
            let newNullElementRef = createRef();
            const newNullElement = {
              render: (
                <img
                  src={NullSymbol}
                  alt="seta"
                  width="100px"
                  style={{
                    position: 'absolute',
                    top: startRef.current.getBoundingClientRect().bottom + 200,
                    left: startRef.current.getBoundingClientRect().left + 350,
                  }}
                  ref={newNullElementRef}
                  key="newNullElement-animation"
                />
              ),
              ref: newNullElementRef,
            };
            let arrow2 = {
              render: (
                <Xarrow
                  start={newState.elements[2].ref}
                  end={newNullElement.ref}
                  color="red"
                  path="grid"
                  showHead={false}
                  strokeWidth={6}
                  startAnchor="right"
                  endAnchor="top"
                  key={uuidv4()}
                />
              ),
            };
            return {
              elements: [...newState.elements, newNullElement],
              arrows: [...newState.arrows, arrow2],
            };
          });
          break;
        case 'pointNewPointerToNewElement':
          setRenderNewWorkFlow((prevState) => {
            const newState = { ...prevState };
            newState.elements.splice(1, 1);
            newState.elements[2].ref = createRef();
            newState.elements[2].render = (
              <>
                <img
                  src={NullSymbol}
                  alt="seta"
                  width="100px"
                  style={{
                    position: 'absolute',
                    top: startRef.current.getBoundingClientRect().bottom + 200,
                    left: startRef.current.getBoundingClientRect().left + 350,
                  }}
                  ref={newState.elements[2].ref}
                  key="newNullElement-animation"
                />
              </>
            );
            newState.arrows[0].render = (
              <Xarrow
                start={newState.elements[0].ref}
                end={newState.elements[1].ref}
                color="red"
                path="straight"
                startAnchor="right"
                endAnchor="left"
                key={uuidv4()}
              />
            );
            newState.arrows[1].render = (
              <Xarrow
                start={newState.elements[1].ref}
                end={newState.elements[2].ref}
                color="red"
                path="grid"
                showHead={false}
                strokeWidth={6}
                startAnchor="right"
                endAnchor="top"
                key={uuidv4()}
              />
            );
            return newState;
          });
          break;
        case 'pointNewElementToStart':
          setRenderNewWorkFlow((prevState) => {
            const newState = { ...prevState };
            newState.elements.pop();
            newState.arrows[1].render =
              elementList.length > 0 ? (
                <Xarrow
                  start={newState.elements[1].ref}
                  end={elementList[0].elementRef}
                  color="red"
                  path="straight"
                  startAnchor="top"
                  endAnchor="bottom"
                  key={uuidv4()}
                />
              ) : (
                <Xarrow
                  start={newState.elements[1].ref}
                  end={nullSymbolRef}
                  color="red"
                  path="grid"
                  showHead={false}
                  strokeWidth={6}
                  _cpy1Offset={-100}
                  _cpy2Offset={-100}
                  startAnchor="top"
                  endAnchor="top"
                  key={uuidv4()}
                />
              );
            return newState;
          });
          break;
        case 'pointStartToNewElement':
          setLinkingNewElement(true);
          setRenderNewWorkFlow((prevState) => {
            const newState = { ...prevState };
            let newArrow = {
              render: (
                <Xarrow
                  start={startRef}
                  end={newState.elements[1].ref}
                  color="red"
                  path="straight"
                  startAnchor="right"
                  endAnchor="left"
                  key={uuidv4()}
                />
              ),
            };
            newState.arrows.push(newArrow);
            return newState;
          });
          break;
        // case 'auxToNextElement':
        //   auxToNextElement();
        case 'findValue':
          return await find(value, true);
        default:
          break;
      }
      if (!hasWaited) {
        await promisedTimeout(animation.timer || animationSpeed);
      }
    };

    for (const animation of animationList) {
      await getAnimation(animation);
    }
    // searchElement();
  };

  const searchElement = async (
    indexOrValue,
    isValue = false,
    callBack = () => {}
  ) => {
    setIsSearching(true);

    let index = 0;
    let myInterval = setInterval(async () => {
      if (elementList.length <= 0) {
        clearInterval(myInterval);
      } else {
        if (
          (isValue && indexOrValue === elementList[index]) ||
          indexOrValue === index
        ) {
          clearInterval(myInterval);
        } else {
          await callBack();
          index++;
          setSearchingAnimation({
            x: referenceList[index]
              ? referenceList[index].elementRef.current.getBoundingClientRect()
                  .right -
                referenceList[
                  index - 1
                ].elementRef.current.getBoundingClientRect().right -
                125
              : nullSymbolRef.current.getBoundingClientRect().right -
                referenceList[
                  index - 1
                ].elementRef.current.getBoundingClientRect().right -
                85,
          });
        }
      }
    }, animationSpeed);
  };

  const insertElement = (indexToInsert, newElement) => {
    setElementList((prevState) => {
      const newList = [...prevState];
      newList.splice(indexToInsert, 0, newElement);
      return newList;
    });
    if (indexToInsert === 0) {
      containerRef.current.scrollTo({
        x: 0,
        animated: true,
      });
    } else if (indexToInsert === elementList.length - 1) {
      containerRef.current.scrollLeft = containerRef.current.scrollWidth;
    }
  };

  const removeElement = (indexOrValue, isValue = false) => {
    if (isValue) {
      setElementList((prevState) =>
        prevState.filter((element) => element !== indexOrValue)
      );
    } else {
      setElementList((prevState) => {
        const newList = [...prevState];
        newList.splice(indexOrValue, 1);
        return newList;
      });
    }
  };

  const handleExecution = async (action, actionType) => {
    try {
      const openModal = async () => {
        setShowBuildMode(true);
        await new Promise((resolve) => {
          setModalPromise(() => resolve);
        });
      };

      if (action === 'initializeList') {
        if (buildMode) {
          setFunctionType('initializeList');
          await openModal();
        }
        setIsInitialized(true);
      } else if (!isInitialized) {
        throw new Error('A lista precisa ser inicializada!');
      }

      if (action === 'destroyList') {
        if (buildMode) {
          setFunctionType('destroyList');
          await openModal();
        }
        setElementList([]);
        setIsInitialized(false);
        return;
      }

      if (action === 'search') {
        switch (actionType) {
          case 'byValue':
            if (buildMode) {
              setFunctionType('serchValue');
              await openModal();
            }
            handleAnimation('findValue', value);
            break;
          case 'byIndex':
            if (buildMode) {
              setFunctionType('searchIndex');
              await openModal();
            }
            console.log(index);
            searchByIndex();
            break;
          default:
            throw new Error('Ocorreu um erro ao buscar o elemento!');
        }
      }

      if (action === 'insert') {
        if (!value) {
          throw new Error('O valor precisa ser informado!');
        }
        switch (actionType) {
          case 'start':
            if (buildMode) {
              setFunctionType('insertStart');
              await openModal();
            }
            let newElement = {
              elementRef: createRef(),
              value: value,
              pointerFromRef: startRef,
            };
            updateCode(insereFunçaoSimples.code);
            await handleAnimation(
              insereFunçaoSimples.executionOrder,
              newElement
            );
            if (elementList.length > 0) {
              updateReferenceInElement(0, newElement.elementRef);
            }
            insertElement(0, newElement);
            setRenderNewWorkFlow({ elements: [], arrows: [] });
            // setShowNewElement(true);
            break;
          case 'end':
            if (buildMode) {
              setFunctionType('insertEnd');
              await openModal();
            }
            setIndex(elementList.length == 0 ? 0 : elementList - 1);
            setInsertButtonClicked(true);
            searchByIndex(elementList.length == 0 ? 0 : elementList);
            break;
          case 'onIndex':
            if (buildMode) {
              setFunctionType('insertIndex');
              await openModal();
            }
            if (index >= 0 && index <= elementList.length - 1) {
              setInsertButtonClicked(true);
              searchByIndex(index);
            } else {
              throw new Error('Índice inválido!');
            }
            break;
          default:
            throw new Error('Ocorreu um erro inesperado!');
        }
      } else if (action === 'remove') {
        switch (actionType) {
          case 'onIndex':
            if (buildMode) {
              setFunctionType('removeIndex');
              await openModal();
            }
            if (index >= 0 && index <= elementList.length - 1) {
              setRemoveButtonClicked(true);
              searchByIndex(index);
            } else {
              throw new Error('Índice inválido!');
            }
            break;
          case 'onValue':
            if (!value) {
              throw new Error('O valor precisa ser informado!');
            }
            if (buildMode) {
              setFunctionType('removeValue');
              await openModal();
            }
            if (elementList.length > 0) {
              setRemoveButtonClicked(true);
              searchByValue(value);
            } else {
              throw new Error('Valor inválido!');
            }
            break;
          default:
            throw new Error('Ocorreu um erro inesperado!');
        }
      }
    } catch (error) {
      alert(error.message);
    }
  };

