import './App.css';
import Element from './components/Element';
import React, { useState, useRef, createRef } from "react";
import CodePanel from './components/CodePanel';
import NumberInput from './components/NumberInput';
import { v4 as uuidv4 } from 'uuid';
import {ScrollView} from 'react-native';
import { View } from 'react-native-web';
import Xarrow, { Xwrapper } from 'react-xarrows';

function App() {
  const [number, setNumber] = useState(0);
  const [elementList, setElementList] = useState([]);
  const [numberToBeRemoved, setNumberToBeRemoved] = useState(0);
  const scrollRef = useRef();

  const insertElement = (index) => {
    const newRef = createRef();
    setElementList((prevState) => {
      const newList = [...prevState];
      newList.splice(index, 0, {
        render: <Element numero={number} ref={newRef} key={uuidv4()} />,
        ref: newRef,
      });
      return newList;
    });


  };

  const insertElementAtBeginning = () => {
    insertElement(0);
    scrollRef.current.scrollTo({
      x: 0,
      animated: true})
  };

  function insertElementAtEnd() {
    insertElement(elementList.length);
    setTimeout(() => {
      scrollRef.current.scrollToEnd({
        animated: true,
      });
    });
  }


  const removeElement = () => {
    setElementList((prevState) =>
      prevState.filter((element) => element.render.props.numero !== numberToBeRemoved)
    );
  };

  return (
    <div className="App">
      <div className="ListPanel">
        <h1>Simulador de Listas Encadeadas </h1>
        <View>
          <ScrollView horizontal={true} ref={scrollRef}>
            <Xwrapper>
              {elementList.map((element, index) => {
                if (index < elementList.length - 1) {
                  return (
                    <>
                      {element.render}
                      <Xarrow
                        key={uuidv4()}
                        start={element.ref}
                        end={elementList[index + 1].ref}
                      />
                    </>
                  );
                } else {
                  return element.render;
                }
              })}
            </Xwrapper>
          </ScrollView>
        </View>
      </div>

      <CodePanel code="CÃ“DIGO TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE TESTE"></CodePanel>

      <div className="ControlPanel">
        <NumberInput setState={setNumber}></NumberInput>
        <button onClick={insertElementAtEnd}>Inserir no final</button>
        <button onClick={insertElementAtBeginning}>Inserir no inicio</button>
        <br/>
        <NumberInput setState={setNumberToBeRemoved}></NumberInput>
        <button onClick={removeElement}>Remover</button>
      </div>
    </div>
  );
}

export default App;
