import styles from "./Element.module.css";
import React, { forwardRef, useEffect, useState } from "react";
import Xarrow from "react-xarrows"

const Element = forwardRef(
  (
    {
      content,
      start = false,
      end = false,
      move = false,
      isDouble = false,
      style = {},
      className = {},
      delayed = false,
    },
    ref
  ) => {
    const [showContent, setShowContent] = useState(false);

    useEffect(() => {
      if (delayed) {
        const timeoutId = setTimeout(() => {
          setShowContent(true);
        }, 4000);

        return () => clearTimeout(timeoutId);
      } else {
        setShowContent(true);
      }
    }, [delayed]);

    return (
      <div
        style={style}
        className={`${className} ${styles.container} ${start && styles.start} ${
          end && styles.end
        }`}
        ref={ref}
        onClick={useXarrow()}
      >
        <div
          className={`${styles.antPointer}`}
          style={{ display: isDouble ? "block" : "none" }}
        >
          {" "}
        </div>
        {showContent && (
          <p className={style.content} style={{ transform: "scaleX(1)" }}>
            {content}
          </p>
        )}
        <div className={`${styles.proxPointer}`}> </div>
        {showContent && (
          <Xarrow
            start={ref} // Ref of the starting element
            end={".arrow-target"} // CSS selector of the target element
            color="blue" // Customize the arrow color
            strokeWidth={3} // Customize the arrow stroke width
            curveness={0.2} // Customize the arrow curveness
          />
        )}
      </div>
    );
  }
);

export default Element;
