export const DraggableComponent = forwardRef((props, ref) => {
  return (
    <motion.div
      drag
      dragConstraints={props.initialConstraints}
      dragElastic={0.5}
      dragSnapToOrigin={!props.canBeDropped}
      whileHover={{ scale: 1.1 }}
      whileTap={{ scale: 0.9 }}
      ref={ref}
    >
      {/* Content of the draggable component */}
      Meu componenete
    </motion.div>
  );
});

export const SnappingComponent = () => {
  return (
    <motion.div
      style={{ position: 'relative', top: 0, left: 0 }}
      whileHover={{ scale: 1.1 }}
      whileTap={{ scale: 0.9 }}
    >
      {/* Content of the snapping component */}
    </motion.div>
  );
};
