import styles from "./Element.module.css";
import React, { forwardRef, useState, useEffect } from "react";
import { useXarrow } from "react-xarrows";

const Element = forwardRef(
  (
    {
      content,
      delayed = false,
      start = false,
      end = false,
      move = false,
      isDouble = false,
      style = {},
      className = {},
    },
    ref
  ) => {
    const [contentState, setContentState] = useState(content);

    useEffect(() => {
      if (delayed) {
        const timer = setTimeout(() => {
          setContentState(content);
        }, 4000);

        return () => clearTimeout(timer); // Cleanup the timer on component unmount
      }
    }, [delayed, content]);

    return (
      <div
        style={style}
        className={`${className} ${styles.container} ${start && styles.start
          } ${end && styles.end}`}
        ref={ref}
        onClick={useXarrow()}
      >
        <div
          className={`${styles.antPointer}`}
          style={{ display: isDouble ? "block" : "none" }}
        ></div>
        <p className={style.content} style={{ transform: "scaleX(1)" }}>
          {content}
        </p>
        <div className={`${styles.proxPointer}`}></div>
      </div>
    );
  }
);

export default Element;
