import React, { useState, useRef, useEffect, createRef } from 'react';
import styles from './ListPanel.module.css';
import Element from '../ListElement';
import { v4 as uuidv4 } from 'uuid';
import Seta from '../../Seta.svg';
import NullSymbol from '../../NullSymbol.svg';
import { ScrollView } from 'react-native';
import CodeBuilderModal from '../CodeBuilderModal/CodeBuilderModal';
import SetaVermelha from '../../SetaVermelha.svg';
import ControlPanel from '../ControlPanel';
import Xarrow, { Xwrapper, useXarrow } from 'react-xarrows';
import { motion } from 'framer-motion/dist/framer-motion';

const ListPanel = ({ className = '', listType = 'simple' }) => {
  const [elementList, setElementList] = useState([]);
  const [renderedList, setRenderedList] = useState([]);
  const [isInitialized, setIsInitialized] = useState(false);
  const [value, setValue] = useState('');
  const [index, setIndex] = useState('');
  const [element, setElement] = useState();
  const [valueToBeSearched, setValueToBeSearched] = useState('');
  const [indexToBeSearched, setIndexToBeSearched] = useState('');
  const [valueFound, setValueFound] = React.useState(true);
  const [animationStopped, setAnimationStopped] = React.useState(false);
  const [showNewElement, setShowNewElement] = useState(false);
  const [showDetailedElements, setShowDetailedElements] = useState(false);
  const [buildMode, setBuildMode] = useState(false);
  const [showNaturalLanguageExplanation, setShowNaturalLanguageExplanation] =
    useState(false);
  const [NaturalLanguageExplanation, setNaturalLanguageExplanation] =
    useState('teste');
  const [currentItemIndex, setCurrentItemIndex] = React.useState(-1);
  const [isSearchByIndex, setIsSearchByIndex] = React.useState(false);
  const [insertButtonClicked, setInsertButtonClicked] = useState(false);
  const [removeButtonClicked, setRemoveButtonClicked] = useState(false);

  const containerRef = useRef(null);
  const elementRef = useRef(null);

  useEffect(() => {
    const container = containerRef.current;
    const element = elementRef.current;

    if (!container || !element) {
      return;
    }

    const containerWidth = container.clientWidth;
    const elementWidth = element.clientWidth;

    if (elementWidth > containerWidth) {
      const scrollAmount = elementWidth - containerWidth;
      container.scrollTo({ left: scrollAmount, behavior: 'smooth' });
    }
  }, []);

  React.useEffect(() => {
    let intervalId;
    const intervalDuration = 2000;

    if (!valueFound && !animationStopped) {
      intervalId = setInterval(() => {
        setCurrentItemIndex((prevIndex) => {
          if (elementList.length > 0) {
            const index = prevIndex + 1;

            if (index >= elementList.length) {
              console.log('O valor não foi encontrado');
              setCurrentItemIndex(-1);
              setAnimationStopped(true);
            }

            if (
              (isSearchByIndex && index == indexToBeSearched) ||
              elementList[index] == valueToBeSearched
            ) {
              if (insertButtonClicked) {
                setTimeout(() => {
                  insertElement(index, value);
                  setInsertButtonClicked(false);
                }, 1500);
              }

              if (removeButtonClicked) {
                setTimeout(() => {
                  if (isSearchByIndex) {
                    removeElement(index);
                    console.log('O elemento foi removido por indice');
                  } else {
                    removeElement(value, true);
                    console.log('O elemento foi removido por valor');
                  }
                  setRemoveButtonClicked(false);
                }, 1500);
              }

              console.log('O valor foi encontrado');
              setValueFound(true);

              setTimeout(() => {
                setCurrentItemIndex(-1);
              }, 2000);
            }

            return index;
          } else {
            if (insertButtonClicked) {
              setTimeout(() => {
                insertElement(index, value);
                setInsertButtonClicked(false);
              }, 1500);
            }

            return 0;
          }
        });
      }, intervalDuration);
    }

    return () => {
      clearInterval(intervalId);
      clearTimeout();
    };
  }, [elementList.length, valueToBeSearched, valueFound, animationStopped]);

  const searchByValue = () => {
    setIsSearchByIndex(false);
    setValueToBeSearched(value);
    setValueFound(false);
    setCurrentItemIndex(-1);
    setAnimationStopped(false);
  };
  const searchByIndex = (indexNum) => {
    setIsSearchByIndex(true);
    indexNum ? setIndexToBeSearched(indexNum) : setIndexToBeSearched(index);
    setValueFound(false);
    setCurrentItemIndex(-1);
    setAnimationStopped(false);
  };

  const auxPointerStyle = {
    position: 'relative',
    left: '10px',
    textAlign: 'center',
  };

  const style = {
    initialPosition: (inRelationToElementRef) => {
      console.log(inRelationToElementRef);
      return {
        display: 'block',
        position: 'absolute',
        top: `${
          inRelationToElementRef.current
            ? inRelationToElementRef.current.getBoundingClientRect().top +
              inRelationToElementRef.current.getBoundingClientRect().height +
              30
            : 0
        }px`,
        left: `${
          inRelationToElementRef.current
            ? inRelationToElementRef.current.getBoundingClientRect().left
            : 0
        }px`,
      };
    },
    move: {},
  };

  const insertElement = (indexToInsert, newValue) => {
    setElementList((prevState) => {
      const newList = [...prevState];
      if (indexToInsert === elementList.length - 1 && indexToInsert !== 0) {
        newList.push(newValue);
      } else {
        newList.splice(indexToInsert, 0, newValue);
      }
      return newList;
    });
    if (indexToInsert === 0) {
      containerRef.current.scrollTo({
        x: 0,
        animated: true,
      });
    } else if (indexToInsert === elementList.length - 1) {
      containerRef.current.scrollLeft = containerRef.current.scrollWidth;
    }
  };

  const removeElement = (indexOrValue, isValue = false) => {
    if (isValue) {
      console.log('aqui');
      setElementList((prevState) =>
        prevState.filter((element) => element !== indexOrValue)
      );
    } else {
      setElementList((prevState) => {
        const newList = [...prevState];
        newList.splice(indexOrValue, 1);
        return newList;
      });
    }
  };

  const handleExecution = (action, actionType) => {
    if (!isInitialized) {
      throw new Error('List not initialized');
    }
    if (action === 'insert') {
      switch (actionType) {
        case 'start':
          setIndex(0);
          setInsertButtonClicked(true);
          searchByIndex(0);
          break;
        case 'end':
          setIndex(elementList.length - 1);
          setInsertButtonClicked(true);
          searchByIndex(elementList.length - 1);
          break;
        case 'onIndex':
          if (index >= 0 && index <= elementList.length - 1) {
            setInsertButtonClicked(true);
            searchByIndex(index);
          } else {
            throw new Error('Invalid action index');
          }
          break;
        case 'onValue':
          break;
        default:
          throw new Error('Invalid action index');
      }
    } else if (action === 'remove') {
      switch (actionType) {
        case 'start':
          setIndex(0);
          setRemoveButtonClicked(true);
          searchByIndex(0);
          break;
        case 'end':
          setIndex(elementList.length - 1);
          setRemoveButtonClicked(true);
          searchByIndex(elementList.length - 1);
          break;
        case 'onIndex':
          if (index >= 0 && index <= elementList.length - 1) {
            setRemoveButtonClicked(true);
            searchByIndex(index);
          } else {
            throw new Error('Invalid index');
          }
          break;
        case 'onValue':
          if (elementList.length > 0) {
            setRemoveButtonClicked(true);
            searchByValue(value);
          } else {
            throw new Error('Invalid Value');
          }
          break;
        default:
          throw new Error('Invalid action index');
      }
    }
  };

  useEffect(() => {
    const mountList = () => {
      switch (listType) {
        case 'simple':
          return elementList.map((element) => {
            const newRef = createRef();
            return {
              render: (
                <div style={{ display: 'flex', alignItems: 'center' }}>
                  <img src={Seta} alt="seta" width="35px" />
                  <Element
                    isDetailed={showDetailedElements}
                    content={element}
                    ref={newRef}
                    key={uuidv4()}
                  />
                </div>
              ),
              ref: newRef,
            };
          });
        default:
          throw new Error('Invalid list type');
      }
    };

    setRenderedList(mountList());
  }, [elementList, listType, showDetailedElements]);

  const windowRef = useRef();

  return (
    <div className={`${className} ${styles.listPanel}`} ref={windowRef}>
      <div
        className="animateElement"
        style={{
          display: showNewElement ? 'block' : 'none',
          position: 'absolute',
          top: 0,
          left: 0,
          height: '100%',
          width: '100%',
          zIndex: 100,
        }}
      >
        {element}
      </div>
      <div className={styles.listView}>
        <h1>Simulador de Listas Encadeadas</h1>
        <Xwrapper>
          <div className={styles.listContainer}>
            <div
              style={{
                display: isInitialized ? 'flex' : 'none',
                overflowX: 'auto', // Add CSS to make the container scrollable horizontally
                whiteSpace: 'nowrap', // Prevent line breaks for the child elements
              }}
              ref={containerRef}
            >
              <div
                style={{ display: isInitialized ? 'flex' : 'none' }}
                className={styles.listStart}
              >
                Início
              </div>
              {renderedList.map((element, index) => (
                <div key={index}>
                  <motion.div
                    key={currentItemIndex}
                    initial={{ opacity: currentItemIndex === index ? 1 : 0 }}
                  >
                    <div style={auxPointerStyle}>Aux</div>
                    <img
                      src={SetaVermelha}
                      alt="seta"
                      height="35px"
                      style={auxPointerStyle}
                    />
                  </motion.div>
                  {element.render}
                </div>
              ))}
              <img
                src={NullSymbol}
                alt="seta"
                width="100px"
                style={{ marginTop: 120 }}
                hidden={!isInitialized}
              />
            </div>
          </div>
        </Xwrapper>

        {insertButtonClicked && (
          <motion.div
            animate={{ opacity: 1 }}
            initial={{ opacity: 0 }}
            transition={{ duration: 1 }}
          >
            <Element content={value} delayed={true} />
          </motion.div>
        )}
      </div>
      <div
        style={{
          display: showNaturalLanguageExplanation ? 'block' : 'none',
          height: '50px',
          width: '100%',
          padding: '10px',
          boxSizing: 'border-box',
          backgroundColor: 'yellow',
        }}
      >
        <p>{NaturalLanguageExplanation}</p>
      </div>
      <div className={`${styles.controlPanel} container text-dark`}>
        <div className="row h-100 align-items-center justify-content-center">
          <div className={`col-12 col-md-3`}>
            <div className="row justify-content-center align-content-center">
              <div className="col-2">
                <input
                  style={{ width: 20 }}
                  type="checkbox"
                  name="detailedElements"
                  id="detailedElements"
                  value={showDetailedElements}
                  onChange={() =>
                    setShowDetailedElements(!showDetailedElements)
                  }
                />
              </div>
              <div className="col-10 justify-content-start">
                <p
                  style={{
                    textAlign: 'start',
                  }}
                >
                  Exibir itens detalhados
                </p>
              </div>
            </div>
            <div className="row justify-content-center align-content-center">
              <div className="col-2">
                <input
                  style={{ width: 20 }}
                  type="checkbox"
                  name="buildMode"
                  id="buildMode"
                  checked={buildMode}
                  onChange={() => setBuildMode(!buildMode)}
                />
              </div>
              <div className="col-10 justify-content-start">
                <p
                  style={{
                    textAlign: 'start',
                  }}
                >
                  Modo construção de código
                </p>
              </div>
            </div>
            <div className="row justify-content-center align-content-center">
              <div className="col-2">
                <input
                  style={{ width: 20 }}
                  type="checkbox"
                  name="naturalLanguage"
                  id="naturalLanguage"
                  value={showNaturalLanguageExplanation}
                  onChange={() =>
                    setShowNaturalLanguageExplanation(
                      !showNaturalLanguageExplanation
                    )
                  }
                />
              </div>
              <div className="col-10 justify-content-start">
                <p
                  style={{
                    textAlign: 'start',
                  }}
                >
                  Exibir explicação em linguagem natural
                </p>
              </div>
            </div>
          </div>
          <div className={`col-12 col-md-6`}>
            <div className="row mb-3 justify-content-center align-content-center">
              <div className="col-4">
                <button
                  className={`${styles.actionButton}`}
                  onClick={() => setIsInitialized(true)}
                >
                  {' '}
                  Iniciar lista
                </button>
              </div>
              <div className="col-4">
                <button className={`${styles.actionButton}`} onClick={() => {}}>
                  {' '}
                  Destruir lista
                </button>
              </div>
              <div className="col-4">
                <button
                  className={`${styles.actionButton}`}
                  onClick={() => searchByIndex()}
                >
                  {' '}
                  Buscar por índice
                </button>
              </div>
            </div>
            <div className="row mb-3 justify-content-center align-content-center">
              <div className="col-4">
                <button
                  className={`${styles.actionButton}`}
                  onClick={() => handleExecution('insert', 'start')}
                >
                  {' '}
                  Inserir no início
                </button>
              </div>
              <div className="col-4">
                <button
                  className={`${styles.actionButton}`}
                  onClick={() => handleExecution('insert', 'end')}
                >
                  {' '}
                  Inserir no fim
                </button>
              </div>
              <div className="col-4">
                <button
                  className={`${styles.actionButton}`}
                  onClick={() => handleExecution('insert', 'onIndex')}
                >
                  {' '}
                  Inserir no índice
                </button>
              </div>
            </div>
            <div className="row justify-content-center">
              <div className="col-4">
                <button
                  className={`${styles.actionButton}`}
                  onClick={() => handleExecution('remove', 'onValue')}
                >
                  {' '}
                  Remover por valor
                </button>
              </div>
              <div className="col-4 ">
                <button
                  className={`${styles.actionButton}`}
                  onClick={() => handleExecution('remove', 'onIndex')}
                >
                  {' '}
                  Remover por índice
                </button>
              </div>
              <div className="col-4">
                <button
                  className={`${styles.actionButton}`}
                  onClick={() => searchByValue()}
                >
                  {' '}
                  Buscar Elemento
                </button>
              </div>
            </div>
          </div>
          <div className={`col-3`}>
            <div className="row mb-3 justify-content-end">
              <div className="col-12 col-md-3 text-start">
                <label htmlFor="">Valor: </label>
              </div>
              <div className="col-12 col-md-8">
                <input
                  type="text"
                  placeholder="Insira o valor"
                  onChange={(e) => setValue(e.target.value)}
                />
              </div>
            </div>
            <div className="row justify-content-end">
              <div className="col-12 col-md-3 text-start">
                <label htmlFor="">Índice: </label>
              </div>
              <div className="col-12 col-md-8">
                <input
                  type="text"
                  placeholder="Insira o índice"
                  onChange={(e) => setIndex(e.target.value)}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
      <CodeBuilderModal
        isOpen={buildMode}
        onClose={() => {
          setBuildMode(false);
        }}
      />
    </div>
  );
};

export default ListPanel;
