import React, { useState, useRef, useEffect, createRef } from "react";
import styles from "./ListPanel.module.css";
import Element from "../ListElement";
import { v4 as uuidv4 } from "uuid";
import Seta from "../../Seta.svg";
import SetaVermelha from "../../SetaVermelha.svg";
import ControlPanel from "../ControlPanel";
import NullSymbol from "../../NullSymbol.svg";
import Xarrow, { Xwrapper, useXarrow } from "react-xarrows";

const ListPanel = ({ className = "", listType = "simple" }) => {
  const [elementList, setElementList] = useState([]);
  const [renderedList, setRenderedList] = useState([]);
  const [isInitialized, setIsInitialized] = useState(false);
  const [value, setValue] = useState("");
  const [index, setIndex] = useState("");
  const [element, setElement] = useState("");
  const [valueToBeSearched, setValueToBeSearched] = useState("");
  const [indexToBeSearched, setIndexToBeSearched] = useState("");
  const [valueFound, setValueFound] = useState(true);
  const [animationStopped, setAnimationStopped] = useState(false);
  const [showNewElement, setShowNewElement] = useState(false);
  const [currentItemIndex, setCurrentItemIndex] = useState(-1);
  const [isSearchByIndex, setIsSearchByIndex] = useState(false);
  const [insertButtonClicked, setInsertButtonClicked] = useState(false);
  const [removeButtonClicked, setRemoveButtonClicked] = useState(false);
  const [isNewElementInserted, setIsNewElementInserted] = useState(false); // New state to track if a new element is inserted
  const containerRef = useRef(null);
  const elementRef = useRef(null);

  useEffect(() => {
    const container = containerRef.current;
    const element = elementRef.current;

    if (!container || !element) {
      return;
    }

    const containerWidth = container.clientWidth;
    const elementWidth = element.clientWidth;

    if (elementWidth > containerWidth) {
      const scrollAmount = elementWidth - containerWidth;
      container.scrollTo({ left: scrollAmount, behavior: "smooth" });
    }
  }, []);

  React.useEffect(() => {
    let intervalId;
    const intervalDuration = 2000;

    if (!valueFound && !animationStopped) {
      intervalId = setInterval(() => {
        setCurrentItemIndex((prevIndex) => {
          if (elementList.length > 0) {
            const index = prevIndex + 1;

            if (index >= elementList.length) {
              console.log("O valor nÃ£o foi encontrado");
              setCurrentItemIndex(-1);
              setAnimationStopped(true);
            }

            if (
              (isSearchByIndex && index === parseInt(indexToBeSearched)) ||
              elementList[index] === valueToBeSearched
            ) {
              if (insertButtonClicked) {
                setTimeout(() => {
                  insertElement(index, value);
                  setInsertButtonClicked(false);
                }, 1500);
              }

              if (removeButtonClicked) {
                setTimeout(() => {
                  if (isSearchByIndex) {
                    removeElement(index);
                    console.log("O elemento foi removido por indice");
                  } else {
                    removeElement(value, true);
                    console.log("O elemento foi removido por valor");
                  }
                  setRemoveButtonClicked(false);
                }, 1500);
              }

              console.log("O valor foi encontrado");
              setValueFound(true);
              setCurrentItemIndex(-1);
              setAnimationStopped(true);
            }

            return index;
          } else {
            return -1;
          }
        });
      }, intervalDuration);
    }

    return () => clearInterval(intervalId);
  }, [
    valueFound,
    elementList,
    animationStopped,
    valueToBeSearched,
    indexToBeSearched,
    isSearchByIndex,
    insertButtonClicked,
    removeButtonClicked,
    value,
  ]);

  const insertElement = (index, value) => {
    const newElement = { id: uuidv4(), value };
    setElementList((prevList) => {
      const newList = [...prevList];
      newList.splice(index, 0, newElement);
      return newList;
    });
  };

  const removeElement = (valueOrIndex, byValue = false) => {
    setElementList((prevList) => {
      const newList = [...prevList];
      let index = byValue
        ? newList.findIndex((element) => element.value === valueOrIndex)
        : valueOrIndex;
      if (index !== -1) {
        newList.splice(index, 1);
      }
      return newList;
    });
  };

  const handleInsertButton = () => {
    if (value && index !== "") {
      setInsertButtonClicked(true);
      setAnimationStopped(false);
      setIsInitialized(true);
    }
  };

  const handleRemoveButton = () => {
    if ((valueToBeSearched || indexToBeSearched !== "") && elementList.length > 0) {
      setRemoveButtonClicked(true);
      setAnimationStopped(false);
      setIsInitialized(true);
    }
  };

  const handleResetButton = () => {
    setElementList([]);
    setRenderedList([]);
    setIsInitialized(false);
    setValue("");
    setIndex("");
    setElement(null);
    setValueToBeSearched("");
    setIndexToBeSearched("");
    setValueFound(true);
    setAnimationStopped(false);
    setShowNewElement(false);
    setCurrentItemIndex(-1);
    setIsSearchByIndex(false);
    setInsertButtonClicked(false);
    setRemoveButtonClicked(false);
    setIsNewElementInserted(false);
  };

  const handleValueChange = (e) => {
    setValue(e.target.value);
  };

  const handleIndexChange = (e) => {
    setIndex(e.target.value);
  };

  const handleValueToBeSearchedChange = (e) => {
    setValueToBeSearched(e.target.value);
  };

  const handleIndexToBeSearchedChange = (e) => {
    setIndexToBeSearched(e.target.value);
  };

  const handleSearchByIndexCheckbox = (e) => {
    setIsSearchByIndex(e.target.checked);
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      handleInsertButton();
    }
  };

  const renderedElements = elementList.map((element) => (
    <Element key={element.id} value={element.value} />
  ));

  const renderNewElement = () => {
    if (!isNewElementInserted) return;
    const newElement = elementList.find((element) => element.value === value);

    if (newElement) {
      return <Element key={newElement.id} value={newElement.value} highlight />;
    }

    return null;
  };

  return (
    <div className={styles.listPanel} ref = { containerRef } >
<div className={styles.listContainer} ref={elementRef}>
{renderedElements}
{renderNewElement()}
</div>
<ControlPanel
value    ={value}
index={index}
valueToBeSearched={valueToBeSearched}
indexToBeSearched={indexToBeSearched}
isSearchByIndex={isSearchByIndex}
insertButtonClicked={insertButtonClicked}
removeButtonClicked={removeButtonClicked}
handleInsertButton={handleInsertButton}
handleRemoveButton={handleRemoveButton}
handleResetButton={handleResetButton}
handleValueChange={handleValueChange}
handleIndexChange={handleIndexChange}
handleValueToBeSearchedChange={handleValueToBeSearchedChange}
handleIndexToBeSearchedChange={handleIndexToBeSearchedChange}
handleSearchByIndexCheckbox={handleSearchByIndexCheckbox}
handleKeyPress={handleKeyPress}
/>
</div >
);
};

export default ListPanel;
